modules['helpcenter/primary/views/validate'] = function(require, exports, module, modules) {
module.exports = Backbone.View.extend({
	events: {

		// Search forms
		'submit #search-form'           : 'fieldSubmit',
		'keypress #search'              : 'fieldTyping',

		// General forms
		'submit .js-validate'           : 'submit',
		'keypress .js-required-field'   : 'typing',
		'change input[type="checkbox"]' : 'removeError',
		'change input[type="radio"]'    : 'removeError',
		'click .dd__link'               : 'removeError',
		'blur .js-required-field:not(.js-noblur)' : 'blur',

		// Forms with only one input field
		'submit .js-validate--field'    : 'fieldSubmit',
		'keypress .js-required-input'   : 'fieldTyping'
	},

	/**
	 * @author Prasanjit Singh <prasanjit.singh@kayako.com>
	 * @description Checks if all the required fields are filled
	 * @param  event
	 */
	submit: function(e) {
		var valid = true;
		var target = $(e.target);

		// Validate text fields
		target.find('.js-required-field').each(function() {
		    if (!$.trim($(this).val()).length) {
		        e.preventDefault();
		        $(this).closest('.textfield__label').addClass('textfield__label--error');
		        valid = false;
		    }
		});

		// Validate checkbox/radio button
		if (target.find('.js-required-checkbox').length) {
			target.find('.js-required-checkbox').each(function() {
			    if (!$(this).find('input').serializeArray().length) {
			        e.preventDefault();
			        $(this).closest('.textfield__label').addClass('textfield__label--error');
			        valid = false;
			    }
			});
		}

		if (valid) {
			$(e.target).find('input[type="submit"]').prop('disabled', 'true').addClass('button--loading');
		}
	},

	/**
	 * @author Prasanjit Singh <prasanjit.singh@kayako.com>
	 * @description Adds error class if user focuses out without filling a required field
	 * @param  event
	 */
	blur: function(e) {
		if(!$.trim(e.target.value).length) {
		    $(e.target).parent('.textfield__label').addClass('textfield__label--error');
		}
	},

	/**
	 * @author Prasanjit Singh <prasanjit.singh@kayako.com>
	 * @description Removes error class if user begins typing
	 * @param  event
	 */
	typing: function(e) {
		var label = $(e.target).parent('.textfield__label');
		if (label.hasClass('textfield__label--error')) {
		    label.removeClass('textfield__label--error');
		}
	},

	/**
	 * @author Prasanjit Singh <prasanjit.singh@kayako.com>
	 * @description Validates if the field is filled in a single field form
	 * @param  event
	 */
	fieldSubmit: function(e) {
		var field = $(e.target).find('.js-required-input');
		if (!$.trim(field.val()).length) {
			e.preventDefault();
			field.addClass('textfield--error');
			return false;
		}
	},

	/**
	 * @author Prasanjit Singh <prasanjit.singh@kayako.com>
	 * @description Removes error class if user begins typing in a single field form
	 * @param  event
	 */
	fieldTyping: function(e) {
		var field = $(e.target);
		if (field.hasClass('textfield--error')) {
		    field.removeClass('textfield--error');
		}
	},

	/**
	 * @author Prasanjit Singh <prasanjit.singh@kayako.com>
	 * @description Removes error class form label
	 * @param  e
	 */
	removeError: function(e) {
		$(e.target).closest('.textfield__label').removeClass('textfield__label--error');
	}
});
};


