modules['helpcenter/primary/models/relatedarticles'] = function(require, exports, module, modules) {
module.exports = Backbone.Model.extend({
	/**
	 * @author Prasanjit Singh <prasanjit.singh@kayako.com>
	 * @param  {Object} options
	 */
    initialize: function (options) {
			this.brandId = options.brandId;
      this.query = options.query;
			this.locale = options.locale;
    },

    /**
     * @author Prasanjit Singh <prasanjit.singh@kayako.com>
     * @return {Object}
     */
    url: function() {
    	return _Payload.portalpath + 'api/v1/helpcenter/search' + this.locale + '&brand_id=' + this.brandId + '&in=articles&tracking=false&query=' + this.query + '+&include=*';
    }
});
};


modules['helpcenter/primary/views/relatedarticles'] = function(require, exports, module, modules) {
/**
 * @fileoverview Shows article suggestions on typing
 */

module.exports = Backbone.View.extend({
	model: require('helpcenter/primary/models/relatedarticles'),

	el: $('#new-conversation-view'),

	events: {
		'keyup #request-subject, #request-msg': 'typing'
	},

	timer: '',

	/**
	 * @author Prasanjit Singh <prasanjit.singh@kayako.com>
	 * @description Checks if suggestions block exists,
	 *	sets active locale if Help Center is localized
	 */
	initialize: function() {
		this.isEnabled = $('#related-articles').length ? true : false;
		this.locale = _Payload.is_localized ? '&locale=' + _Payload.activelocale : '';
	},

	/**
	 * @author Prasanjit Singh <prasanjit.singh@kayako.com>
	 * @description Triggers fetch() with a delay (400 ms)
	 */
	typing: function() {
		var view = this;
		if (view.isEnabled) {
			var query = $('#request-subject').val() + ' ' + $('#request-msg').val().replace(/\n/g, ' ');

			if (query.length > 3) {
				clearTimeout(view.timer);
		    view.timer = setTimeout(function() {
					view.fetch(query);
		    }, 400);
			} else {
				$('.autocomplete--aside').hide();
			}
		}
	},

	/**
	 * @author Prasanjit Singh <prasanjit.singh@kayako.com>
	 * @description Gets related articles hitting search API
	 * @param {String} query
	 */
	fetch: function(query) {
		var view = this;

		// Show loading animation
		$('.autocomplete--aside')
			.show()
			.addClass('searching')
			.removeClass('no-results');

		// Get results
		new view.model({
			'brandId': _Payload.brand_id,
			'query': query,
			'locale': view.locale
		}).fetch({
			success: function(response) {
				view.render(response.attributes.data);
			},
			error: function(response) {
				view.clearSuggestions();
			}
		});
	},

	/**
	 * @author Prasanjit Singh <prasanjit.singh@kayako.com>
	 * @description Renders suggestions in the sidebar
	 * @param {Object} data
	 */
	render: function(data) {
		if (!_.isEmpty(data)) {
			var liTpl = _.template($('#tpl-related-article-item').html());

			// Create a list of results
			var list = '';
			_.each(data, function(item) {
				list += liTpl({ link: item.link, title: item.title });
			});

			// Hide loading animation and append the list
			$('.autocomplete--aside').removeClass('searching');
			$('#related-articles-list').empty().append(list);
		} else {
			this.clearSuggestions();
		}
	},

	/**
	 * @author Prasanjit Singh <prasanjit.singh@kayako.com>
	 * @description Clears sidebar suggestions
	 */
	clearSuggestions: function() {
		$('.autocomplete--aside').removeClass('searching').addClass('no-results');
		$('#related-articles-list').empty();
	}
});
};


