modules['helpcenter/primary/views/vote'] = function(require, exports, module, modules) {
var redirect = require('helpcenter/primary/views/redirect');

module.exports = Backbone.View.extend({
  model: require('helpcenter/primary/models/vote'),

  el: $('#vote-view'),

  events: {
    'click .js-vote-option:not(.is-selected)': 'vote',
    'mousedown .js-vote-option:not(.is-selected)': 'iconPress'
  },

  /**
   * @author Prasanjit Singh <prasanjit.singh@kayako.com>
   * @description Sets article ID recieved from router
   * @param  {Object} options
   */
  initialize: function(options) {
    this.options = options;
  },

  /**
   * @author Prasanjit Singh <prasanjit.singh@kayako.com>
   * @description Animates the vote icon
   * @param {Object} e
   */
  iconPress: function(e) {
    var $icon = $(e.target).closest('.js-vote-option');
    $icon.addClass('is-pressed');
    setTimeout(function() {
      $icon.removeClass('is-pressed');
    }, 100);
  },

  /**
   * @author Prasanjit Singh <prasanjit.singh@kayako.com>
   * @description Saves user's vote if user is logged in
   * otherwise shows error message
   * @param  {Object} e
   */
  vote: function(e) {
    var view = this;

    // Show error toast if user is not logged in
    if ($('#is-loggedin').val() != 1) {
      view.toast('error');
      return false;
    } else {
      var $elem = $(e.target).closest('.js-vote-option');

      // Save user vote
      new view.model({
        'articleId': view.options.articleId.split('-')[0],
        'vote': $elem.data('value')
      }).save({}, {
        error: function(model, xhr) {
          redirect('article', view.options.articleId, xhr.status);
        }
      });

      // Highlight selected option
      $('.js-vote-option').removeClass('is-selected');
      $elem.addClass('is-selected');

      // Show thanking message
      view.toast('success');
    }
  },

  /**
   * @author Prasanjit Singh <prasanjit.singh@kayako.com>
   * @description Shows success/error message
   * @param {String} msgType
   */
  toast: function(msgType) {
    var delayTime = (msgType === 'success') ? 150 : 0;

    // Show toast
    setTimeout(function() {
      var msgTpl = _.template($('#tpl-vote-msg').html());
      var msg = msgTpl({ msgType: msgType});
      $('#vote-options').fadeOut(250, function() {
        $('#vote-msg').html(msg).fadeIn(250);
      });
    }, delayTime);

    // Hide toast
    setTimeout(function() {
      $('#vote-msg').fadeOut(250, function() {
        $('#vote-options').fadeIn(250);
      });
    }, 3000);
  }
});
};


