    set: function(e) {
        var elem = $(e.target);
        var root = elem.parents('.drill-wrap ');
        root.find('.js-drill-selected').text(elem.text());
        root.find('#article-parent').val(elem.data('id'));
        root.find('.drilldown').removeClass('dd--open');
        root.find('.drill-default').removeClass('drill-focus');
    },

