modules['helpcenter/primary/views/survey'] = function(require, exports, module, modules) {
module.exports = Backbone.View.extend({
    el: $('#survey'),

    events: {
        'change .rating--input': 'change'
    },

    /**
     * @author Prasanjit Singh <prasanjit.singh@kayako.com>
     * @description Displays good/bad message
     */
    change: function() {
        if ($('#rating--good').is(':checked')) {
            $('.survey-title--bad').removeClass('is-visible');
            $('.survey-title--good').addClass('is-visible');
        } 
        else if ($('#rating--bad').is(':checked')) {
            $('.survey-title--good').removeClass('is-visible');
            $('.survey-title--bad').addClass('is-visible');
        }
    }
});

};


