modules['helpcenter/primary/views/profile'] = function(require, exports, module, modules) {
/**
 * @fileoverview Client-side functionality of profile page:
 * - Change/remove avatar
 * - Add/remove fields
 * - Verify emails
 * - Connect/disconnect social profiles
 */

var LinkProfile = require('helpcenter/primary/models/link-profile');
var DeleteIdentity = require('helpcenter/primary/models/deletefields');

module.exports = Backbone.View.extend({
	el: '#profile-container',

	events: {
		'click #change-avatar'                    : 'changeAvatar',
		'click #delete-avatar'                    : 'deleteAvatar',
		'click .js-add-field'                     : 'add',
		'click .js-delete-field'                  : 'delete',
		'click .js-verify-email'									: 'verifyEmail',
		'click .js-social-connect'                : 'connect',
		'click .js-social-disconnect'             : 'disconnect',
		'change .textfield, .file-input, .select' : 'change',
		'submit .js-validate'                     : 'submit'
	},

	unsaved: false,

	/**
	 * @author Prasanjit Singh <prasanjit.singh@kayako.com>
	 * @description Opens file input dialog and selects file to be uploaded
	 */
	changeAvatar: function() {
		var $fileInput = $('#avatar-input');
		$fileInput.click();
		$fileInput.on('change', function() {
			$('#avatar-hint').text($fileInput.val().split('\\').pop());
		});
	},

	/**
	 * @author Prasanjit Singh <prasanjit.singh@kayako.com>
	 * @description Deletes user avatar
	 */
	deleteAvatar: function() {
		var $deleteBtn = $('#delete-avatar');

		// Disable button and show loading animation
		$deleteBtn
		  .addClass('is-disabled button--loading')
		  .prop('disabled', true)
		  .after(_.template($('#tpl-loader').html()));

		// Send API request
		new DeleteIdentity({ 'id': 1 }).destroy({
		  success: function() {
		    var $avatar = $('#edit-avatar');
		    $avatar.attr('src', $avatar.attr('src') + '/?refresh');
		    $deleteBtn.removeClass('button--loading');
		  },
		  error: function() {
		    $deleteBtn.removeClass('is-disabled button--loading').prop('disabled', false);
		  }
		});
	},

	/**
	 * @author Prasanjit Singh <prasanjit.singh@kayako.com>
	 * @description Adds a new phone or email field
	 */
	add: function(e) {
		var $el = $(e.target);
		var tplField = _.template($('#tpl-field').html());
		var tplFieldHtml = tplField({
		  type: $el.data('add')
		});

		// Append the field
		$(tplFieldHtml).appendTo($el.parent()).slideDown(100);

		// Hide 'Add' link
		$el.fadeOut(100);
	},

	/**
	 * @author Prasanjit Singh <prasanjit.singh@kayako.com>
	 * @description Removes phone/email
	 * @param  {Object} e
	 */
	delete: function(e) {
		var $deleteLink = $(e.target);
		var fieldType = $deleteLink.data('remove');
		var fieldId = $deleteLink.data('id');

		// Show loading animation
		$deleteLink
		  .addClass('is-disabled button--loading')
		  .after(_.template($('#tpl-loader').html()));

		// Send API request
		new DeleteIdentity({
		  'id': fieldId
		}, {
		  'fieldId': fieldId,
		  'fieldType': fieldType
		}).destroy({
		  success: function() {
		    $deleteLink.parents('.js-field-value').remove();
		  },
		  error: function() {
		    $deleteLink
		      .removeClass('is-disabled button--loading')
		      .next().remove();
		  }
		});
	},

	/**
	 * @author Prsanjit Singh <prasanjit.singh@kayako.com>
	 * @description Sends verification email
	 * @param {Object} e
	 */
	verifyEmail: function(e) {
		var view = this;
		var $verifyLink = $(e.target);

		// Disable link and show loading animation
		$verifyLink.addClass('is-disabled');
		$verifyLink.next('.js-delete-field')
			.addClass('button--loading')
			.after(_.template($('#tpl-loader').html()));

		// Send API request
		new LinkProfile({
			'id': $verifyLink.data('id'),
			'query': 'verifyemail'
		}).save({}, {
			success: function() {
				view.notification($verifyLink, 'success');
			},
			error: function() {
				view.notification($verifyLink, 'error');
			}
		});
	},

	/**
	 * @author Prsanjit Singh <prasanjit.singh@kayako.com>
	 * @description Shows success/error notification (auto-hide)
	 * @param {Object} $verifyLink
	 * @param {String} errorType
	 */
	notification: function($verifyLink, errorType) {
		// Re-enable link and remove loading animation
		$verifyLink.removeClass('is-disabled');
		$verifyLink.next('.js-delete-field').removeClass('button--loading');

		// Show auto-hide alert bar
		var alertTpl = _.template($('#tpl-alert').html());
		$('body').append(alertTpl({ type: errorType }));
		setTimeout(function() {
			$('.js-alert-box').addClass('a-alert-box--out');
			setTimeout(function() {
				$('.js-alert-box').remove();
			}, 200);
		}, 3000);
	},

	/**
	 * @author Prsanjit Singh <prasanjit.singh@kayako.com>
	 * @description Connects facebook/twitter account to user profile
	 * @param {Object} e
	 */
	connect: function(e) {
		var $btn = $(e.target);

		// Show loading animation
		$btn
		  .addClass('is-disabled button--loading')
		  .prop('disabled', true)
		  .after(_.template($('#tpl-loader').html()));

		// Send API request
		var account = $btn.data('account');
		new LinkProfile({
		  'account': account,
			'query': 'connect'
		}).fetch({
		  data: {
		    callback: account === 'twitter' ? '/profile/linktwitter/' : '/facebook/connect/'
		  },
		  success: function(model, response) {
		    window.location.href = response.data.link;
		  },
		  error: function() {
				$btn.removeClass('is-disabled button--loading').prop('disabled', false);
		  }
		});
	},

	/**
	 * @author Prasanjit Singh <prasanjit.singh@kayako.com>
	 * @description Disconnects facebook/twitter account from user profile
	 * @param {Object} e
	 */
	disconnect: function(e) {
		var $link = $(e.target);
		$link.addClass('is-disabled');

		// Send API request
		var account = $link.data('account');
		new LinkProfile({
			'id': $link.data('id'),
			'account': account,
			'query': 'disconnect'
		}).destroy({
			success: function() {
				// Replace 'Disconnect' link with 'Connect' button
				$link.parent('.js-social-connection').html('<input class="button button--wide u-inlineblock js-social-connect" data-account="' + account + '" type="button" value="Connect">');
			},
			error: function() {
				$link.removeClass('is-disabled');
			}
		});
	},

	/**
	 * @author Prasanjit Singh <prasanjit.singh@kayako.com>
	 * @description Show alert if there are unsaved changes
	 */
	change: function() {
		var view = this;
		view.unsaved = true;

		window.onbeforeunload = function() {
			// Remove any animation from social connect buttons
		  $('.button--wide.button--loading')
				.attr('disabled', false)
				.removeClass('is-disabled button--loading');

		  if (view.unsaved) {
		    return 'You have some unsaved edits.';
		  }
		};
	},

	/**
	 * @author Prasanjit Singh <prasanjit.singh@kayako.com>
	 * @description Removes alert bar and focuses invalid field
	 * if form contains any error
	 */
	submit: function() {
		this.unsaved = false;
		$('#profile').find('.js-required-field').each(function() {
		  if (!$(this).val().trim().length) {
				$(this).focus();
				$('.alert-box--overlay').remove();
				return false;
		  }
		});
	}
});
};


