modules['helpcenter/primary/views/addemail'] = function(require, exports, module, modules) {
module.exports = Backbone.View.extend({
	el: $('#addemail-container'),

	events: {
		'submit #add-email': 'email',
		'submit #add-password': 'password'
	},

	/**
	 * @author Bishwanath Jha <bishwanath.jha@kayako.com>
	 * @description Handle email submit
	 */
	email: function(e) {
		e.preventDefault();
		var email = $('#modal-email').val();
		var token = $('#modal-token').val();
		$('#duplicateerrormessage').hide();

		if (email.length) {
			$(e.target).find('input[type="submit"]').prop('disabled', true).addClass('button--loading');
			$.ajax({
				url: _Payload.url + 'account/email',
				type: 'POST',
				data: {email: email, token: token},
				success: function(response) {
					if (response.status === 201) {
						window.location.href = _Payload.url;
						return true;
					}
				},
				error: function(response) {
					// If we have received 401 which means AUTHENTICATION_FAILED, Redirect user to login page
					if (response.status === 401) {
						$('#add-email').fadeOut(200);
						$('#add-password').fadeIn(200);
						return false;
					} else if (response.status === 403) {
						var message = response.responseJSON.notifications.error;
						if (message.length) {
							$('.modal__wrap').fadeOut('200', function() {
								$(this).remove();
								var tpl_error = _.template($('#tpl-password-alert').html());
								var tpl_html = tpl_error({message: message});
								$('.alert-box').remove();
								$('.body').prepend(tpl_html);
								setTimeout(function() {
									$('.alert-box').fadeOut('300', function() {
										$(this).remove();
									});;
								}, 3000);
							});
						} else {
							window.location.href = _Payload.url + 'login';
							return false;
						}

					} else if (response.status === 301 || response.status === 454 || response.status === 404) {
						window.location.href = _Payload.url + 'login';
						return false;
					} else {
						// Otherwise there is some error with email
						$('#modal-email').closest('.textfield__label').addClass('textfield__label--error');
						$(e.target).find('input[type="submit"]').prop('disabled', false).removeClass('button--loading');
					}
				}
			});
		} else {
			$('#modal-email').closest('.textfield__label').addClass('textfield__label--error');
		}
	},
	/**
	 * @author Bishwanath Jha <bishwanath.jha@kayako.com>
	 * @description Handle password submit
	 */
	password: function(e) {
		e.preventDefault();
		$(e.target).find('input[type="submit"]').prop('disabled', true).addClass('button--loading');
		var email = $('#modal-email').val();
		var password = $('#modal-password').val();
		var token = $('#modal-token').val();
		$('#duplicateerrormessage').hide();

		if (password == '') {
			$('#modal-password').closest('.textfield__label').addClass('textfield__label--error');
			return false;
		}

		$.ajax({
			url: _Payload.url + 'account/verify',
			type: 'POST',
			data: {email: email, token: token, password: password},
			success: function(response) {
				if (response.status === 201) {
					window.location.href = _Payload.url;
					return true;
				}
			},
			error: function(response) {
				var status = response.status;
				$(e.target).find('input[type="submit"]').prop('disabled', false).removeClass('button--loading');
				if (status === 401 ) {
					var tpl_error = _.template($('#tpl-password-alert').html());
					var tpl_html = tpl_error({message: response.responseJSON.notifications.warning});
					$('.alert-box').remove();
					$('.modal__body').prepend(tpl_html);
				} else if (response.status === 403) {
					var message = response.responseJSON.notifications.error;
					if (message.length) {
						$('.modal__wrap').fadeOut('200', function() {
							$(this).remove();
							var tpl_error = _.template($('#tpl-password-alert').html());
							var tpl_html = tpl_error({message: message});
							$('.alert-box').remove();
							$('.body').prepend(tpl_html);
							setTimeout(function() {
								$('.alert-box').fadeOut('300', function() {
									$(this).remove();
								});;
							}, 3000);
						});
					} else {
						window.location.href = _Payload.url + 'login';
						return false;
					}

				}
				else if (status === 301 || response.status === 454 || status === 404) {
					window.location.href = _Payload.url + 'login';
					return false;
				}
				// Too many requests
				else if (status === 429) {
					$('.modal__wrap').fadeOut('200', function() {
						$(this).remove();
						var tpl_error = _.template($('#tpl-password-alert').html());
						var tpl_html = tpl_error({message: response.responseJSON.notifications.warning});
						$('.alert-box').remove();
						$('.body').prepend(tpl_html);
						setTimeout(function() {
							$('.alert-box').fadeOut('300', function() {
								$(this).remove();
							});;
						}, 3000);
					});
				} else {
					// Otherwise there is some error with password
					$('#modal-password').closest('.textfield__label').addClass('textfield__label--error');
				}
			}
		});
	}
});
};


