modules['helpcenter/primary/models/customize/templates'] = function(require, exports, module, modules) {
module.exports = Backbone.Model.extend({
    /**
     * @author Prasanjit Singh <prasanjit.singh@kayako.com>
     * @param  {Object}
     */
    initialize: function(options) {
        this.query = options.query;
        this.brandId = options.brandId;
        if (options.item) {
            this.idAttribute = 'item';
            this.item = options.item;
        }
    },

    /**
     * @author Prasanjit Singh <prasanjit.singh@kayako.com>
     * @return {Obejct}
     */
    url: function() {
        switch (this.query) {
            // Fetches data of all templates
            case 'allTpl':
                return _Payload.portalpath + 'api/v1/brands/' + this.brandId + '/templates?limit=20&include=*';
            // Fetches or updates single template
            case 'singleTpl':
                return _Payload.portalpath + 'api/v1/brands/' + this.brandId + '/templates/' + this.item + '?include=*';
            // Resets a template
            case 'resetTpl':
                return _Payload.portalpath + 'api/v1/brands/' + this.brandId + '/templates/' + this.item + '/restore?include=*';
        }
    }
});

};


